import { useState, useEffect } from 'react'

import { getResults, createResult } from '../../utils/result';
import { getCourses, createCourse } from '../../utils/course';

export default function ResultsPage() {

    const [courseName, setCourseName] = useState("")
    const [studentName, setStudentName] = useState("")
    const [score, setScore] = useState("")
    const [results, setResults] = useState([])
    const [error, setError] = useState(false)

    const [courses, setCourses] = useState([])

    useEffect(() => {
        getAll();
        getAllCourses();
    }, []);

    async function getAll() {
        const resultsAll = await getResults();
        setResults(resultsAll)
    }

    async function getAllCourses() {
        const coursesAll = await getCourses();
        setCourses(coursesAll);
      }

    function handleChange(e) {
        if (e.target.name === "courseName") setCourseName(e.target.value);
        if (e.target.name === "studentName") setStudentName(e.target.value);
        if (e.target.name === "score") setScore(e.target.value);

    }

    async function handleSubmit(e) {
        e.preventDefault()
        let body = {
            courseName: courseName,
            studentName: studentName,
            score: score,
        }
        await createResult(body).then((error) => {
            alert("New result has been added")
            setCourseName("")
            setStudentName("")
            setScore("")
            getAll()
        })
    }


  return (
    <>
        <h1>Results Page</h1>
        <hr />
        <form>
            <div>
            <span>Course name</span><br/>
            {/* <input name="courseName" type="text" value={courseName} autoComplete="off" onChange={handleChange} required /> */}
            <select name="courseName">
                {courses.map((c) => 
                <option>{c.name}</option>
                )}
            </select>
            </div>

            <div>
            <span>Student name</span><br/>
            <input name="studentName" type="text" value={studentName} autoComplete="off" onChange={handleChange} required />
            </div>

            <div>
            <span>Score</span><br/>
            <input name="score" type="text" value={score} onChange={handleChange} required />
            </div>
            <br/>
            {error ?
            <label>All fields must be filled</label> : ""}
            <br/>
            <button onClick={handleSubmit}>Submit</button>
        </form>
        <div className='Result'>
            <table className='table'>
                <thead>
                    <tr>
                <th>Course</th>
                <th>Student</th>
                <th>Score</th>

                    </tr>
                </thead>
                <tbody>
                    {results.map((r) => {
                    return (
                        <tr key={r._id}>
                        <td>{r.courseName}</td>
                        <td>{r.studentName}</td>
                        <td>{r.score}</td>
                        </tr>
                    );
                    })}
                </tbody>
            </table>
        </div>
    </>
  )
}
